var block = function(){
	$('.block-unit-branding').mouseover(function(){
		$(this).animate({
			width:465,height:465
		}, 1300);
		$('.resume--products').fadeIn(1000,function(){
			$(this).addClass('block-animated');
		});
	});
	$('.block-unit-branding').mouseleave(function(){
		$(this).animate({width:150,height:150});
		$('.resume--products').fadeOut(200,function(){
			$(this).removeClass('block-animated');
		});
	});
}

$(document).ready(block);